import{H as t,a as _,o as e,c as i,b as s,e as a,I as n,J as c}from"./index-a68a51f7.js";const l="/Dr_Sevar/assets/lips1-71da77fd.jpg",r="/Dr_Sevar/assets/lips2-fb98ec3b.jpg",d="/Dr_Sevar/assets/lips3-220fce9e.jpg",p="/Dr_Sevar/assets/lips4-0ad59cf4.jpg",m={},b={class:"container"},f={class:"info"},h=c('<img src="'+l+'" alt="" class="info__images"><h1 class="info__title">Контурная пластика губ</h1><p class="info__text"><span class="bold"> Контурная пластика губ – </span> современная эффективная процедура, которая поможет быстро и без операции увеличить губы, придать им соблазнительную естественную форму, добавить объем, сделать пухлыми и волнующими <br> <br> Суть методики заключается в следующем: в область губ при помощи иглы или атравматичной канюли вводятся филлеры. Это специальные препараты, основным компонентом которых является гиалуроновая кислота - естественным компонент организма человека, отвечающий за выработку коллагена и эластина в коже. <br> <br> Филлеры увеличивают объем и корректируют форму губ. Эффект выглядит абсолютно натурально и проявляется сразу после процедуры. </p><h3 class="info__headline">Преимущества коррекции губ филлерами</h3><p class="info__text"><span class="bold"> Эффективно. </span> Инъекции филлеров мгновенно заполняют недостающие объемы тканей верхней и нижней губы, что позволяет получить желаемый результат. <br> <br><span class="bold"> Безопасно. </span> В клиниках «Реформа» контурная пластика проводится только с помощью филлеров от проверенных ведущих производителей. Все препараты изготовлены на основе гиалуроновой кислоты – естественного компонента организма, поэтому их введение безопасно. <br> <br><span class="bold"> Безболезненно. </span> Процедуры всегда проводятся с заботой о самой чувствительной коже. Чтобы снять возможные болевые ощущения при введении препаратов, используются специальные анестезирующие кремы. <br> <br><span class="bold"> Быстро. </span> Процедура длится всего 15 минут без учета действия анестезии. <br> <br><span class="bold"> Надолго. </span> Результат сохраняется на 6-8 месяцев, затем процедуру можно повторить. Если делать инъекции гиалуроновой кислотой у косметолога регулярно, то с каждый последующим сеансом эффект будет становиться все более длительным. Это объясняется тем, что препарат стимулирует обновление тканей и выработку коллагена и эластина. </p><h3 class="info__headline">Противопоказания</h3>',6),g={class:"info__text"},u=s("br",null,null,-1),x=s("br",null,null,-1),v=s("br",null,null,-1),S=s("br",null,null,-1),j=c('<h3 class="info__headline">Результат процетуры</h3><div class="info__text-img"><img src="'+r+'" alt="" class="info__text-img"><img src="'+d+'" alt="" class="info__text-img"><img src="'+p+'" alt="" class="info__text-img"></div>',2);function D(N,V){const o=_("fa");return e(),i("div",b,[s("div",f,[h,s("p",g,[a(o,{icon:"diamond",class:"dimond__icon"}),n(" Воспаления и повредения кожи в зоне проведения процедуры; "),u,a(o,{icon:"diamond",class:"dimond__icon"}),n(" Беременность и период лактации; "),x,a(o,{icon:"diamond",class:"dimond__icon"}),n(" Наличие онкологических заболеваний; "),v,a(o,{icon:"diamond",class:"dimond__icon"}),n(" ОРВИ, ОРЗ. "),S]),j])])}const B=t(m,[["render",D]]);export{B as default};
